nt"},{"anonymous":false,"inputs":[{"indexed":false,"internalType":"uint256","name":"when","type":"uint256"},{"indexed":false,"internalType":"uint8","name":"exposition","type":"uint8"},{"indexed":false,"internalType":"uint256","name":"oldNAV","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"NAV","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"usdcNAV","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"longNAV","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"shortNAV","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"totalsupply","type":"uint256"}],"name":"navEvent","type":"event"},{"anonymous":false,"inputs":[{"indexed":true,"internalType":"address","name":"account","type":"address"},{"indexed":false,"internalType":"uint256","name":"when","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"amount","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"nav","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"preop_supply","type":"uint256"}],"name":"withdrawEvent","type":"event"},{"inputs":[],"name":"GMX_controller","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"USDC","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"WETH","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"tokenAddress","type":"address"}],"name":"acceptToken","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"owner","type":"address"},{"internalType":"address","name":"spender","type":"address"}],"name":"allowance","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType